import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, TextInput, Modal } from 'react-native';
export default function OpsPendingLoanDetailScreen({ navigation, route }) {
  const { updateLoan, updateLoanAmount } = useLoan();
  const [client, setClient] = useState(route.params?.client || {
    name: 'John Doe',
    age: 26,
    cid: '001',
    email: 'johndoe@gmail.com',
    number: '+639123456789',
    address: '123 Mabini Street, Metro Manila, 1100, Philippines',
    documents: ['Valid Id', 'ORCR', 'Land Title', 'Deeds of Assignment', 'Signed Documents'],
    financials: {
      income: '₱60,000',
      expenses: '₱25,000',
      savings: '₱150,000',
      ratio: '30%',
    },
    employment: {
      company: 'ABC Corporation',
      position: 'Software Developer',
    },
    loan: {
      requested: '₱50,000',
      recommended: '₱100,000',
      rate: '5.0%',
      repayment: 'Monthly',
      creditScore: 720,
      risk: {
        payment: 'Fair',
        dti: '45%',
        overall: 'Moderate',
      },
    },
  });

  const [showEditModal, setShowEditModal] = useState(false);
  const [editedAmount, setEditedAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loanData, setLoanData] = useState(client.loan);

  // Initialize data from route params
  useEffect(() => {
    if (route.params?.client) {
      const initialClient = {
        ...client,
        ...route.params.client,
        loan: {
          ...client.loan,
          requested: '₱50,000', // Default value if not provided
          recommended: '₱100,000',
          rate: '5.0%',
          repayment: 'Monthly',
          creditScore: 720,
          risk: {
            payment: 'Fair',
            dti: '45%',
            overall: 'Moderate',
          }
        }
      };
      setClient(initialClient);
      setLoanData(initialClient.loan);
    }
  }, [route.params?.client]);

  // Don't render until we have client data
  if (!client || !loanData) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      {/* Topbar */}
      <View style={styles.topbar}>
        <Text style={styles.logo}><Text style={{ color: '#0066ff' }}>Loan</Text>Siya</Text>
        <Text style={styles.role}>Operations Manager</Text>
        <Text style={styles.logout}>Logout</Text>
      </View>

      {/* Body */}
      <View style={styles.content}>
        {/* LEFT SIDE */}
        <View style={styles.left}>
          <Text style={styles.sectionTitle}>Client Profile</Text>
          <Text style={styles.subTitle}>Personal Details</Text>
          <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
          <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
          <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
          <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
          <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
          <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>

          <Text style={[styles.subTitle, { marginTop: 16 }]}>Uploaded Document</Text>
          {client.documents.map((doc, i) => (
            <View key={i} style={styles.documentRow}>
              <Text>{doc}</Text>
              <TouchableOpacity style={styles.viewBtn}><Text>View</Text></TouchableOpacity>
            </View>
          ))}
        </View>

        {/* RIGHT SIDE */}
        <View style={styles.right}>
          <Text style={styles.sectionTitle}>Financial Record</Text>
          <Text>Monthly Income {client.financials.income}</Text>
          <Text>Monthly Expenses {client.financials.expenses}</Text>
          <Text>Saving and Assets {client.financials.savings}</Text>
          <Text>Debt to Income Ratio {client.financials.ratio}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Employment or Business info</Text>
          <Text>{client.employment.company}</Text>
          <Text>{client.employment.position}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Loan History</Text>
          <TouchableOpacity style={styles.viewBtn}><Text>View</Text></TouchableOpacity>

          {/* Loan Evaluation Box */}
          <View style={styles.loanBox}>
            <Text style={styles.bold}>Borrower Request</Text>
            <Text>{loanData.requested}</Text>
            <Text style={styles.bold}>Recommended Loan Amount</Text>
            <Text>{loanData.recommended}</Text>
            <Text><Text style={styles.bold}>Interest Rate:</Text> {loanData.rate}</Text>
            <Text><Text style={styles.bold}>Repayment Method:</Text> {loanData.repayment}</Text>
          </View>

          {/* Credit & Risk */}
          <View style={styles.creditBox}>
            <Text style={styles.bold}>Credit Score</Text>
            <Text style={styles.score}>{client.loan.creditScore}</Text>
            <Text style={[styles.bold, { marginTop: 10 }]}>Risk Assessment</Text>
            <Text>Payment History: {client.loan.risk.payment}</Text>
            <Text>Debt-to-Income Ratio: {client.loan.risk.dti}</Text>
            <Text>Overall Creditworthiness: {client.loan.risk.overall}</Text>
          </View>

          {/* Action Buttons */}
          <View style={styles.actionRow}>
            <TouchableOpacity style={styles.backBtn} onPress={() => navigation.goBack()}>
              <Text>← Back</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.editBtn}
              onPress={() => {
                setEditedAmount(loanData.requested.replace('₱', '').replace(',', ''));
                setShowEditModal(true);
              }}
            >
              <Text>Edit</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.declineBtn}
              onPress={() => {
                const updatedClient = {
                  ...client,
                  status: 'Declined',
                  cid: client.cid || route.params?.client?.cid
                };
                setSuccess('Loan application declined');
                navigation.navigate('OpsPendingList', { updatedClient });
              }}
            >
              <Text style={{ color: '#fff' }}>Decline</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.approveBtn}
              onPress={() => {
                const updatedClient = {
                  ...client,
                  status: 'Approved',
                  cid: client.cid || route.params?.client?.cid
                };
                setSuccess('Loan application approved');
                navigation.navigate('OpsPendingList', { updatedClient });
              }}
            >
              <Text style={{ color: '#fff' }}>Approve</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
      {/* Edit Modal */}
      <Modal
        visible={showEditModal}
        transparent={true}
        animationType="fade"
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Edit Loan Amount</Text>
            <Text style={styles.modalSubtitle}>
              Maximum amount: {loanData.requested}
            </Text>
            
            <TextInput
              style={styles.amountInput}
              value={editedAmount}
              onChangeText={(text) => {
                setEditedAmount(text.replace(/[^0-9]/g, ''));
                setError('');
              }}
              keyboardType="numeric"
              placeholder="Enter amount"
            />
            
            {error ? <Text style={styles.errorText}>{error}</Text> : null}
            
            <View style={styles.modalButtons}>
              <TouchableOpacity
                style={styles.cancelBtn}
                onPress={() => {
                  setShowEditModal(false);
                  setError('');
                }}
              >
                <Text>Cancel</Text>
              </TouchableOpacity>
              
              <TouchableOpacity
                style={styles.saveBtn}
                onPress={() => {
                  const requestedAmount = parseInt(loanData.requested.replace('₱', '').replace(',', ''));
                  const newAmount = parseInt(editedAmount);
                  
                  if (!newAmount || newAmount <= 0) {
                    setError('Please enter a valid amount');
                    return;
                  }
                  
                  if (newAmount > requestedAmount) {
                    setError(`Amount cannot exceed borrower's request of ${loanData.requested}`);
                    return;
                  }
                  
                  // Format the new amount with peso sign and commas
                  const formattedAmount = `₱${newAmount.toLocaleString()}`;
                  
                  // Update the loan data
                  setLoanData({
                    ...loanData,
                    requested: formattedAmount
                  });
                  
                  setShowEditModal(false);
                  setSuccess('Loan amount updated successfully');
                }}
              >
                <Text style={{ color: '#fff' }}>Save Changes</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>

      {/* Success Message */}
      {success ? (
        <View style={styles.successMessage}>
          <Text style={styles.successText}>{success}</Text>
        </View>
      ) : null}
    </ScrollView>
  );
}

