import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, ActivityIndicator } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  documents: ['Valid Id', 'ORCR', 'Land Title', 'Deeds of Assignment', 'Signed Documents'],
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A'
};

export default function OpsClientProfileScreen({ navigation, route }) {
  const [client, setClient] = useState(null);
  const [loanData, setLoanData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { refreshClientData } = useLoan();
  const cid = route.params?.client?.cid;

  useEffect(() => {
    if (cid) {
      loadClientData();
    }
  }, [cid]);

  const loadClientData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load client profile data
      const clientData = await refreshClientData(cid);
      setClient({
        ...defaultClient,
        ...clientData
      });

      // Load loan application data
      const response = await fetch(`http://localhost:5600/client-loan-data/${cid}`);
      if (!response.ok) {
        throw new Error('Failed to fetch loan data');
      }
      const loanAppData = await response.json();
      setLoanData(loanAppData);

    } catch (err) {
      console.error('Error loading data:', err);
      setError('Failed to load data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <ActivityIndicator size="large" color="#0066ff" />
        <Text style={styles.loadingText}>Loading client data...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryBtn} onPress={loadClientData}>
          <Text style={styles.retryText}>Retry</Text>
        </TouchableOpacity>
      </View>
    );
  }

  if (!client) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>Client not found</Text>
        <TouchableOpacity 
          style={styles.backBtn} 
          onPress={() => navigation.goBack()}
        >
          <Text>← Back to List</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      {/* Topbar */}
      <View style={styles.topbar}>
        <Text style={styles.logo}><Text style={{ color: '#0066ff' }}>Loan</Text>Siya</Text>
        <Text style={styles.role}>Operations Manager</Text>
      </View>

      {/* Body */}
      <View style={styles.content}>
        {/* LEFT SIDE */}
        <View style={styles.left}>
          <Text style={styles.sectionTitle}>Client Profile</Text>
          <Text style={styles.subTitle}>Personal Details</Text>
          <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
          <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
          <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
          <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
          <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
          <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>

          <Text style={[styles.subTitle, { marginTop: 16 }]}>Uploaded Document</Text>
          {client.documents.map((doc, i) => (
            <View key={i} style={styles.documentRow}>
              <Text>{doc}</Text>
              <TouchableOpacity style={styles.viewBtn}><Text>View</Text></TouchableOpacity>
            </View>
          ))}
        </View>

        {/* RIGHT SIDE */}
        <View style={styles.right}>
          <Text style={styles.sectionTitle}>Financial Record</Text>
          <Text>Monthly Income ₱{client.financial.monthlyIncome.toLocaleString()}</Text>
          <Text>Monthly Expenses ₱{client.financial.monthlyExpenses.toLocaleString()}</Text>
          <Text>Saving and Assets ₱{client.financial.assets.toLocaleString()}</Text>
          <Text>Debt to Income Ratio {client.financial.dti}%</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Employment or Business info</Text>
          <Text>{client.employer}</Text>
          <Text>{client.position}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Current Loan Application</Text>
          {loanData ? (
            <View style={styles.loanInfoContainer}>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Approved Amount:</Text>
                <Text style={styles.loanValue}>₱{loanData.approvedAmount}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Recommended Amount:</Text>
                <Text style={styles.loanValue}>₱{loanData.recommendedAmount}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Purpose:</Text>
                <Text style={styles.loanValue}>{loanData.purpose}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Description:</Text>
                <Text style={styles.loanValue}>{loanData.description}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Loan Term:</Text>
                <Text style={styles.loanValue}>{loanData.term} months</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Repayment Method:</Text>
                <Text style={styles.loanValue}>{loanData.repaymentMethod}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Interest Rate:</Text>
                <Text style={styles.loanValue}>{loanData.interestRate}%</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Amount Due:</Text>
                <Text style={styles.loanValue}>₱{loanData.amountDue}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Credit Score:</Text>
                <Text style={styles.loanValue}>{loanData.creditScore}</Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Risk Category:</Text>
                <Text style={[styles.loanValue,
                  {color: loanData.riskCategory === 'Good' ? '#28a745' : '#dc3545'}]}>
                  {loanData.riskCategory}
                </Text>
              </View>
              <View style={styles.loanInfoRow}>
                <Text style={styles.loanLabel}>Recommendation:</Text>
                <Text style={[styles.loanValue,
                  {color: loanData.recommendation === 'APPROVE' ? '#28a745' : '#dc3545'}]}>
                  {loanData.recommendation}
                </Text>
              </View>
            </View>
          ) : (
            <Text>No loan application data available</Text>
          )}

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Loan History</Text>
          <TouchableOpacity
            style={styles.viewBtn}
            onPress={() => navigation.navigate('LoanHistory', {
              clientId: client.cid,
              userRole: 'ops'
            })}
          >
            <Text>View</Text>
          </TouchableOpacity>

          <View style={styles.actionRow}>
            <TouchableOpacity 
              style={styles.backBtn} 
              onPress={() => navigation.goBack()}
            >
              <Text>← Back to List</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.refreshBtn} 
              onPress={loadClientData}
            >
              <Text>↻ Refresh</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  loanInfoContainer: {
    backgroundColor: '#fff',
    padding: 15,
    borderRadius: 8,
    marginBottom: 15,
    borderWidth: 1,
    borderColor: '#ddd',
  },
  loanInfoRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingVertical: 6,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
  },
  loanLabel: {
    fontWeight: 'bold',
    color: '#666',
    flex: 1,
  },
  loanValue: {
    flex: 1,
    textAlign: 'right',
  },
  container: { 
    backgroundColor: '#f6f6f6',
    flex: 1 
  },
  centerContent: {
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  errorText: {
    color: '#dc3545',
    fontSize: 16,
    marginBottom: 15,
  },
  retryBtn: {
