import React, { useState } from 'react';
import { View, Text, TextInput, FlatList, TouchableOpacity, StyleSheet, ActivityIndicator, useWindowDimensions, Platform } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';

export default function ClientListScreen({ navigation }) {
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  
  const [search, setSearch] = useState('');
  const { loans, loading, error } = useLoan();

  const filteredClients = loans.filter(client =>
    client.name.toLowerCase().includes(search.toLowerCase()) || client.cid.includes(search)
  );

  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={[styles.row, isMobile && styles.rowMobile]}
      onPress={() => isMobile && navigation.navigate('ClientProfile', {
        client: item
      })}
    >
      {isMobile ? (
        // Mobile layout
        <View style={styles.mobileContent}>
          <Text style={styles.mobileName}>{item.name}</Text>
          <Text style={styles.mobileCid}>CID: {item.cid}</Text>
        </View>
      ) : (
        // Desktop layout
        <View style={styles.rowContent}>
          <Text style={[styles.cell, styles.cidCell]}>{item.cid}</Text>
          <Text style={[styles.cell, styles.nameCell]}>{item.name}</Text>
          <View style={styles.actionCell}>
            <TouchableOpacity
              style={styles.viewBtn}
              onPress={() => navigation.navigate('ClientProfile', {
                client: item
              })}
            >
              <Text style={styles.viewBtnText}>View</Text>
            </TouchableOpacity>
          </View>
        </View>
      )}
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <ActivityIndicator size="large" color="#0066ff" />
        <Text style={styles.loadingText}>Loading clients...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>{error}</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={[styles.headerRow, isMobile && styles.headerRowMobile]}>
        <Text style={[styles.title, isMobile && styles.titleMobile]}>Client List</Text>
        <TouchableOpacity
          style={[styles.dashboardBtn, isMobile && styles.dashboardBtnMobile]}
          onPress={() => navigation.navigate('LoanOfficerDashboard')}
        >
          <Text style={[styles.btnText, isMobile && styles.btnTextMobile]}>Go to Dashboard</Text>
        </TouchableOpacity>
      </View>

      <View style={[styles.searchBar, isMobile && styles.searchBarMobile]}>
        <TextInput
          style={[styles.searchInput, isMobile && styles.searchInputMobile]}
          placeholder="Search by name or CID"
          value={search}
          onChangeText={setSearch}
        />
      </View>

      {/* Table Headers - Only show on desktop */}
      {!isMobile && (
        <View style={styles.tableHeader}>
          <View style={styles.rowContent}>
            <Text style={[styles.headerCell, styles.cidCell]}>CID</Text>
            <Text style={[styles.headerCell, styles.nameCell]}>Full Name</Text>
            <View style={styles.actionCell}>
              <Text style={styles.headerCell}></Text>
            </View>
          </View>
        </View>
      )}

      <FlatList
        data={filteredClients}
        keyExtractor={(item) => item.cid}
        renderItem={renderItem}
        ListEmptyComponent={() => (
          <View style={styles.emptyContainer}>
            <Text style={styles.emptyText}>
              {search ? 'No clients match your search' : 'No clients found'}
            </Text>
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  rowContent: {
    flexDirection: 'row',
    flex: 1,
    alignItems: 'center',
  },
  cidCell: {
    width: '25%',
    textAlign: 'left',
  },
  nameCell: {
    width: '50%',
    textAlign: 'left',
  },
  actionCell: {
    width: '25%',
    alignItems: 'flex-end',
  },
  // Mobile specific styles
  headerRowMobile: {
    flexDirection: 'column',
    gap: 12,
    alignItems: 'flex-start',
    marginBottom: 16,
  },
  titleMobile: {
    fontSize: 18,
  },
  dashboardBtnMobile: {
    paddingVertical: 8,
    paddingHorizontal: 12,
  },
  btnTextMobile: {
    fontSize: 14,
  },
  searchBarMobile: {
    marginBottom: 10,
  },
  searchInputMobile: {
    padding: 8,
    fontSize: 14,
  },
  rowMobile: {
    padding: 12,
    marginBottom: 8,
    backgroundColor: '#fff',
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#e9ecef',
  },
  mobileContent: {
    flex: 1,
  },
  mobileName: {
    fontSize: 16,
    fontWeight: '500',
    marginBottom: 4,
    color: '#212529',
  },
  mobileCid: {
    fontSize: 14,
    color: '#6c757d',
  },
  viewBtnText: {
    color: '#495057',
    fontSize: 14,
    fontWeight: '500',
  },
  container: {
    padding: Platform.select({ web: 20, default: 16 }),
    backgroundColor: '#f6f6f6',
    flex: 1,
  },
  centerContent: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666',
  },
  errorText: {
    color: '#dc3545',
    fontSize: 16,
    marginBottom: 15,
    textAlign: 'center',
  },
  emptyContainer: {
    padding: 20,
    alignItems: 'center',
  },
  emptyText: {
    fontSize: 16,
    color: '#666',
  },
  headerRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  title: {
    fontSize: 22,
    fontWeight: 'bold',
  },
  dashboardBtn: {
    backgroundColor: '#0066ff',
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderRadius: 6,
  },
  btnText: {
    color: '#fff',
    fontWeight: 'bold',
  },
  searchBar: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  searchInput: {
    backgroundColor: '#fff',
    borderRadius: 6,
    padding: 10,
    flex: 1,
  },
  tableHeader: {
    flexDirection: 'row',
    backgroundColor: '#e9ecef',
    paddingVertical: 12,
    paddingHorizontal: Platform.select({ web: 16, default: 12 }),
    marginBottom: 4,
    borderRadius: 8,
    alignItems: 'flex-start',
  },
headerCell: {
  fontWeight: 'bold',
  fontSize: 16,
  color: '#495057',
  },
  row: {
    flexDirection: 'row',
    backgroundColor: '#fff',
    paddingVertical: Platform.select({ web: 12, default: 10 }),
    paddingHorizontal: Platform.select({ web: 16, default: 12 }),
    marginBottom: 8,
    borderRadius: 8,
    alignItems: 'flex-start',
    borderWidth: 1,
    borderColor: '#e9ecef',
  },
  cell: {
    paddingHorizontal: 6,
    fontSize: Platform.select({ web: 16, default: 14 }),
    color: '#212529',
  },
  viewBtn: {
    backgroundColor: '#e9ecef',
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderRadius: 6,
  }
});
