import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, TextInput, Modal, useWindowDimensions, Platform } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';
import { clientAPI, API_BASE_URL, DOCUMENT_CATEGORIES } from '../../services/api';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  input: {
    paymentHistory: 0,
    creditUtilization: 0,
    creditHistoryLength: 0,
    creditMix: 0,
    newInquiries: 0
  },
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A',
  loans: {
    approved: {
      amount: 0,
      status: 'None'
    },
    pending: {
      amount: 0,
      status: 'None'
    },
    loanHistory: []
  },
  loanBalance: {
    amount: 0,
    dueDate: 'N/A'
  },
  documents: {
    applicationDocs: DOCUMENT_CATEGORIES.APPLICATION_DOCS,
    signedDocuments: []
  }
};

export default function OpsPendingLoanDetailScreen({ navigation, route }) {
  const { updateLoan, updateLoanAmount } = useLoan();
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const [client, setClient] = useState(null);
  const [loanData, setLoanData] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editedAmount, setEditedAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [latestRequest, setLatestRequest] = useState(null);

  const fetchLoanData = async (cid) => {
    try {
      const response = await fetch(`http://localhost:5600/client-loan-data/${cid}`);
      if (!response.ok) {
        throw new Error('Failed to fetch loan data');
      }
      const data = await response.json();
      setLoanData(data);
    } catch (err) {
      console.error('Error fetching loan data:', err);
      setError('Failed to fetch loan data');
    }
  };

  // Update latestRequest whenever client changes
  useEffect(() => {
    if (client?.loans?.loanHistory?.length > 0) {
      const latest = [...client.loans.loanHistory].sort((a, b) =>
        new Date(b.dateApplied) - new Date(a.dateApplied)
      )[0];
      setLatestRequest(latest);
    }
  }, [client]);

  useEffect(() => {
    console.log('Route params:', route.params);
    if (route.params?.client) {
      console.log('Initializing client with:', route.params.client);
      const initialClient = {
        ...defaultClient,
        ...route.params.client,
        documents: {
          applicationDocs: route.params.client.documents?.applicationDocs || defaultClient.documents.applicationDocs,
          signedDocuments: route.params.client.documents?.signedDocuments || []
        }
      };
      console.log('Setting client with CID:', initialClient.cid);
      setClient(initialClient);
      
      // Fetch loan data when client is set
      if (initialClient.cid) {
        fetchLoanData(initialClient.cid);
      }
    }
  }, [route.params?.client]);

  if (!client) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }

  const handleDecline = async () => {
try {
  setError('');
  console.log('Declining loan for CID:', client.cid);
  
  // First check if API is accessible
  try {
    const healthCheck = await fetch('http://localhost:5600/clients');
    if (!healthCheck.ok) {
      throw new Error('API server is not responding properly');
    }
  } catch (err) {
    throw new Error('API server is not running. Please start the credit-scoring-api server first.');
  }

  const result = await clientAPI.processLoanDecision(client.cid, 'declined');
  console.log('API Response:', result);
  
  await updateLoan(client.cid, {
    status: 'Declined',
    decidedAt: new Date().toISOString()
  });
  console.log('Local state updated');
  
  setSuccess('Loan application declined');
  setTimeout(() => {
    navigation.replace('OpsPendingList');
  }, 1000);
} catch (err) {
  console.error('Error declining loan:', err);
  setError(err.message || 'Failed to decline loan');
  setTimeout(() => setError(''), 3000); // Clear error after 3 seconds
}
};

  const handleApprove = async () => {
    try {
      setError('');
      console.log('Approving loan for CID:', client.cid);
      
      // First check if API is accessible
      try {
        const healthCheck = await fetch('http://localhost:5600/clients');
        if (!healthCheck.ok) {
          throw new Error('API server is not responding properly');
        }
      } catch (err) {
        throw new Error('API server is not running. Please start the credit-scoring-api server first.');
      }

      // Save the current approved amount before approval
      const currentApprovedAmount = loanData?.approvedAmount || '0';

      // Exclude "documents" from the client object
      const { documents, ...clientWithoutDocuments } = client;

      const result = await clientAPI.processLoanDecision(client.cid, 'approved', {
        approvedAmount: loanData?.approvedAmount,
        ...clientWithoutDocuments
      });
      console.log('API Response:', result);
  
  await updateLoan(client.cid, {
    status: 'Approved',
    decidedAt: new Date().toISOString(),
    approvedAmount: currentApprovedAmount
  });
  console.log('Local state updated');
  
  setSuccess('Loan application approved');
  setTimeout(() => {
    navigation.replace('OpsPendingList');
  }, 1000);
} catch (err) {
  console.error('Error approving loan:', err);
  setError(err.message || 'Failed to approve loan');
  setTimeout(() => setError(''), 3000); // Clear error after 3 seconds
}
};

  const handleSaveEdit = async () => {
    try {
      const newAmount = parseInt(editedAmount);
      
      if (!newAmount || newAmount <= 0) {
        setError('Please enter a valid amount');
        return;
      }
      
      const maxAmount = loanData?.approvedAmount || 0;
      
      if (newAmount > maxAmount) {
        setError(`Amount cannot exceed approved amount of ${maxAmount.toLocaleString()}`);
        return;
      }

      if (client.loans?.loanHistory && client.loans.loanHistory.length > 0) {
        const latest = [...client.loans.loanHistory].sort((a, b) =>
          new Date(b.dateApplied) - new Date(a.dateApplied)
        )[0];

        // Calculate amount due (5% interest)
        const interest = newAmount * 0.05; // 5% interest
        const total = newAmount + interest;

        // Create updated latest request
        const updatedLatest = {
          ...latest,
          amount: newAmount,
          amountDue: {
            total
          }
        };

        // Create updated client data
        const updatedClient = {
          ...client,
          loans: {
            ...client.loans,
            loanHistory: client.loans.loanHistory.map(loan =>
              loan.dateApplied === latest.dateApplied ? updatedLatest : loan
            )
          }
        };
        
        await updateLoanAmount(client.cid, newAmount);
        setLatestRequest(updatedLatest); // Update latest request state immediately
        setClient(updatedClient);
        
        // Refresh loan data from API
        await fetchLoanData(client.cid);
        
        setShowEditModal(false);
        setSuccess('Loan amount updated successfully');
      }
    } catch (error) {
      setError('Failed to update loan amount');
      console.error('Error updating loan amount:', error);
    }
  };

  const calculateScore = () => {
    const weights = {
      paymentHistory: 0.35,
      creditUtilization: 0.30,
      creditHistoryLength: 0.15,
      creditMix: 0.10,
      newInquiries: 0.10,
    };

    const input = client.input;
    
    // Normalize inputs to 0-1 scale
    const normalized = {
      paymentHistory: input.paymentHistory / 100,
      creditUtilization: 1 - (input.creditUtilization / 100),
      creditHistoryLength: Math.min(input.creditHistoryLength / 60, 1),
      creditMix: input.creditMix / 100,
      newInquiries: 1 - (input.newInquiries / 100),
    };

    const weightedSum =
      weights.paymentHistory * normalized.paymentHistory +
      weights.creditUtilization * normalized.creditUtilization +
      weights.creditHistoryLength * normalized.creditHistoryLength +
      weights.creditMix * normalized.creditMix +
      weights.newInquiries * normalized.newInquiries;

    // Scale to 300-850 range
    return Math.round(300 + weightedSum * 550);
  };

  const getRiskCategory = (score) => {
    if (score >= 800) return 'Exceptional';
    if (score >= 740) return 'Very Good';
    if (score >= 670) return 'Good';
    if (score >= 580) return 'Fair';
    return 'Poor';
  };

  return (
    <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
      {/* Topbar */}
      <View style={[styles.topbar, isMobile && styles.topbarMobile]}>
        <Text style={[styles.logo, isMobile && styles.logoMobile]}>
          <Text style={{ color: '#0066ff' }}>Loan</Text>Siya
        </Text>
        <Text style={[styles.role, isMobile && styles.roleMobile]}>Operations Manager</Text>
      </View>

      <View style={[styles.content, isMobile && styles.contentMobile]}>
        {/* LEFT SIDE */}
        <View style={[styles.left, isMobile && styles.leftMobile]}>
          <Text style={[styles.sectionTitle, isMobile && styles.sectionTitleMobile]}>Client Profile</Text>
          <View style={[styles.detailsContainer, isMobile && styles.detailsContainerMobile]}>
            <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Personal Details</Text>
            <View style={[styles.personalDetailsGrid, isMobile && styles.personalDetailsGridMobile]}>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Name:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.name}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Age:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.age}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>CID:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.cid}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Email:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]} numberOfLines={isMobile ? 2 : 1}>{client.email}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Number:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.number}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Address:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]} numberOfLines={isMobile ? 3 : 2}>{client.address}</Text>
              </View>
            </View>
          </View>

          <Text style={[styles.subTitle, { marginTop: 12 }, isMobile && styles.subTitleMobile]}>Uploaded Documents</Text>
          <View style={[styles.documentCategory, isMobile && styles.documentCategoryMobile]}>
            <Text style={[styles.categoryText, isMobile && styles.categoryTextMobile]}>Application Documents</Text>
            <TouchableOpacity
              style={[styles.previewBtn, isMobile && styles.previewBtnMobile]}
              onPress={async () => {
                try {
                  if (!client.cid) {
                    console.error('No client CID available');
                    alert('Error: Client ID not found');
                    return;
                  }
                  console.log('Fetching documents for client:', client.cid);
                  const documents = await clientAPI.getLatestDocuments(client.cid);
                  console.log('Documents received:', documents);
                  if (documents.length > 0) {
                    navigation.navigate('DocumentPreview', {
                      cid: client.cid,
                      title: 'Application Documents',
                      documents: documents.filter(doc => !doc.type.includes('pdf')),
                      latestDate: documents[0].date
                    });
                  } else {
                    alert('No application documents found');
                  }
                } catch (error) {
                  console.error('Error loading documents:', error);
                  alert('Error loading documents. Please try again.');
                }
              }}
            >
              <Text style={[styles.previewText, isMobile && styles.previewTextMobile]}>View</Text>
            </TouchableOpacity>
          </View>

          <View style={[styles.documentCategory, isMobile && styles.documentCategoryMobile]}>
            <Text style={[styles.categoryText, isMobile && styles.categoryTextMobile]}>Signed Documents</Text>
            <TouchableOpacity
              style={[styles.previewBtn, isMobile && styles.previewBtnMobile]}
              onPress={async () => {
                try {
                  const documents = await clientAPI.getLatestDocuments(client.cid);
                  const signedDocs = documents.filter(doc => doc.type.includes('pdf'));
                  if (signedDocs.length > 0) {
                    navigation.navigate('DocumentPreview', {
                      cid: client.cid,
                      title: 'Signed Documents',
                      documents: signedDocs,
                      latestDate: documents[0].date
                    });
                  } else {
                    alert('No signed documents available yet. Documents will be available after loan approval.');
                  }
                } catch (error) {
                  console.error('Error loading documents:', error);
                  alert('Error loading documents. Please try again.');
                }
              }}
            >
              <Text style={[styles.previewText, isMobile && styles.previewTextMobile]}>View</Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* RIGHT SIDE */}
        <View style={[styles.right, isMobile && styles.rightMobile]}>
          <View style={[styles.detailsContainer, isMobile && styles.detailsContainerMobile]}>
            <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Financial Record</Text>
            <View style={styles.financialGrid}>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Monthly Income:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.financial.monthlyIncome.toLocaleString()}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Monthly Expenses:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.financial.monthlyExpenses.toLocaleString()}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Savings and Assets:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.financial.assets.toLocaleString()}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Debt to Income Ratio:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.financial.dti}%</Text>
              </View>
            </View>
          </View>

          <View style={[styles.detailsContainer, { marginTop: 16 }, isMobile && styles.detailsContainerMobile]}>
            <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Employment or Business Info</Text>
            <View style={styles.employmentGrid}>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Employer:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.employer}</Text>
              </View>
              <View style={styles.detailRow}>
                <Text style={[styles.detailLabel, isMobile && styles.detailLabelMobile]}>Position:</Text>
                <Text style={[styles.detailValue, isMobile && styles.detailValueMobile]}>{client.position}</Text>
              </View>
            </View>
          </View>

          <View style={[styles.loanHistorySection, isMobile && styles.loanHistorySectionMobile]}>
            <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Loan History</Text>
            <TouchableOpacity
              style={[styles.viewBtn, isMobile && styles.viewBtnMobile]}
              onPress={() => navigation.navigate('LoanHistory', {
                clientId: client.cid,
                userRole: 'ops'
              })}
            >
              <Text style={[styles.viewBtnText, isMobile && styles.viewBtnTextMobile]}>View</Text>
            </TouchableOpacity>
          </View>

          <View style={[styles.loanDetails, isMobile && styles.loanDetailsMobile]}>
            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Approved by Loan Officer</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.approvedAmount?.toLocaleString() || '0'}</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Recommended Loan Amount (Based on Credit Score)</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.recommendedAmount?.toLocaleString() || '0'}</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Purpose of Loan</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.purpose || 'N/A'}</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Description of Purpose</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]} numberOfLines={isMobile ? 3 : 2}>{loanData?.description || 'N/A'}</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Loan Term (in months)</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.term || 'N/A'}</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Repayment Method</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.repaymentMethod || 'Monthly'}</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Interest Rate</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.interestRate || '5.0'}%</Text>
            </View>

            <View style={styles.loanDetailRow}>
              <Text style={[styles.subTitle, isMobile && styles.subTitleMobile]}>Amount Due</Text>
              <Text style={[styles.loanDetailValue, isMobile && styles.loanDetailValueMobile]}>{loanData?.amountDue || '0'}</Text>
            </View>
          </View>

          <View style={[styles.creditBox, isMobile && styles.creditBoxMobile]}>
            <Text style={[styles.subTitle, { marginBottom: 4 }, isMobile && styles.subTitleMobile]}>Credit Score</Text>
            <Text style={[styles.creditScore, isMobile && styles.creditScoreMobile]}>{loanData?.creditScore || calculateScore()}</Text>
            <Text style={[styles.subTitle, { marginTop: 12 }, isMobile && styles.subTitleMobile]}>Risk Category</Text>
            <Text style={[{
              color: ['Exceptional', 'Very Good', 'Good'].includes(loanData?.riskCategory || getRiskCategory(calculateScore())) ? '#2ecc71' : '#e74c3c'
            }, isMobile && styles.riskCategoryMobile]}>{loanData?.riskCategory || getRiskCategory(calculateScore())}</Text>
            <Text style={[styles.subTitle, { marginTop: 12 }, isMobile && styles.subTitleMobile]}>Recommendation</Text>
            <Text style={[{
              color: loanData?.recommendation === 'APPROVE' ? '#2ecc71' : '#e74c3c'
            }, isMobile && styles.recommendationMobile]}>{loanData?.recommendation || (calculateScore() >= 670 ? 'Approved' : 'Not Approved')}</Text>
          </View>

          {/* Action Buttons */}
          <View style={[styles.actionRow, isMobile && styles.actionRowMobile]}>
            <TouchableOpacity
              style={[styles.backBtn, isMobile && styles.backBtnMobile]}
              onPress={() => navigation.goBack()}>
              <Text style={[styles.buttonText, isMobile && styles.buttonTextMobile]}> Back</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[styles.editBtn, isMobile && styles.editBtnMobile]}
              onPress={() => {
                const maxAmount = loanData?.approvedAmount?.toString() || '0';
                setEditedAmount(maxAmount);
                setShowEditModal(true);
              }}
            >
              <Text style={[styles.buttonText, isMobile && styles.buttonTextMobile]}>Edit</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[styles.declineBtn, isMobile && styles.declineBtnMobile]}
              onPress={handleDecline}
            >
              <Text style={[styles.buttonTextWhite, isMobile && styles.buttonTextWhiteMobile]}>Decline</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[styles.approveBtn, isMobile && styles.approveBtnMobile]}
              onPress={handleApprove}
            >
              <Text style={[styles.buttonTextWhite, isMobile && styles.buttonTextWhiteMobile]}>Approve</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>

      {/* Edit Modal */}
      <Modal
        visible={showEditModal}
        transparent={true}
        animationType="fade"
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Edit Loan Amount</Text>
            <Text style={styles.modalSubtitle}>
              Maximum amount: {loanData?.approvedAmount?.toLocaleString() || '0'}
            </Text>
            
            <TextInput
              style={styles.amountInput}
              value={editedAmount}
              onChangeText={(text) => {
                setEditedAmount(text.replace(/[^0-9]/g, ''));
                setError('');
              }}
              keyboardType="numeric"
              placeholder="Enter amount"
            />
            
            {error ? <Text style={styles.errorText}>{error}</Text> : null}
            
            <View style={styles.modalButtons}>
              <TouchableOpacity
                style={styles.cancelBtn}
                onPress={() => {
                  setShowEditModal(false);
                  setError('');
                }}
              >
                <Text>Cancel</Text>
              </TouchableOpacity>
              
              <TouchableOpacity
                style={styles.saveBtn}
                onPress={handleSaveEdit}
              >
                <Text style={{ color: '#fff' }}>Save Changes</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>


      {/* Success/Error Messages */}
      {success ? (
        <View style={styles.successMessage}>
          <Text style={styles.successText}>{success}</Text>
        </View>
      ) : null}
      {error ? (
        <View style={styles.errorMessage}>
          <Text style={styles.errorText}>{error}</Text>
        </View>
      ) : null}
    </ScrollView>
  );
}

