export function LoanProvider({ children }) {
  const [loans, setLoans] = useState(initialLoans);

  const updateLoan = (cid, updates) => {
    setLoans(prevLoans =>
      prevLoans.map(loan =>
        loan.cid === cid
          ? { ...loan, ...updates }
          : loan
      )
    );
  };

  const updateLoanAmount = (cid, newAmount) => {
    setLoans(prevLoans =>
      prevLoans.map(loan =>
        loan.cid === cid
          ? {
              ...loan,
              loans: {
                ...loan.loans,
                pending: {
                  ...loan.loans.pending,
                  amount: newAmount
                }
              }
            }
          : loan
      )
    );
  };

  return (
    <LoanContext.Provider value={{ loans, updateLoan, updateLoanAmount }}>
      {children}
    </LoanContext.Provider>
  );
}

export function useLoan() {
  const context = useContext(LoanContext);
  if (!context) {
    throw new Error('useLoan must be used within a LoanProvider');
  }
  return context;
}
