import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, TextInput, Modal } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  input: {
    paymentHistory: 0,
    creditUtilization: 0,
    creditHistoryLength: 0,
    creditMix: 0,
    newInquiries: 0
  },
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A',
  loans: {
    approved: {
      amount: 0,
      status: 'None'
    },
    pending: {
      amount: 0,
      status: 'None'
    },
    loanHistory: []
  },
  loanBalance: {
    amount: 0,
    dueDate: 'N/A'
  }
};

export default function OpsPendingLoanDetailScreen({ navigation, route }) {
  const { updateLoan, updateLoanAmount } = useLoan();
  const [client, setClient] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editedAmount, setEditedAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    if (route.params?.client) {
      const initialClient = {
        ...defaultClient,
        ...route.params.client
      };
      setClient(initialClient);
    }
  }, [route.params?.client]);

  if (!client) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }

  const handleDecline = () => {
    updateLoan(client.cid, { status: 'Declined' });
    setSuccess('Loan application declined');
    setTimeout(() => {
      navigation.replace('OpsPendingList');
    }, 1000);
  };

  const handleApprove = () => {
    updateLoan(client.cid, { status: 'Approved' });
    setSuccess('Loan application approved');
    setTimeout(() => {
      navigation.replace('OpsPendingList');
    }, 1000);
  };

  const handleSaveEdit = () => {
    const newAmount = parseInt(editedAmount);
    
    if (!newAmount || newAmount <= 0) {
      setError('Please enter a valid amount');
      return;
    }
    
    if (client.loans?.loanHistory && client.loans.loanHistory.length > 0) {
      const latestRequest = [...client.loans.loanHistory].sort((a, b) =>
        new Date(b.dateApplied) - new Date(a.dateApplied)
      )[0];
      
      if (newAmount > latestRequest.amount) {
        setError(`Amount cannot exceed borrower's request of ₱${latestRequest.amount.toLocaleString()}`);
        return;
      }

      // Create updated client data
      const updatedClient = {
        ...client,
        loans: {
          ...client.loans,
          loanHistory: client.loans.loanHistory.map((loan, index) => {
            if (index === client.loans.loanHistory.length - 1) {
              return { ...loan, amount: newAmount };
            }
            return loan;
          })
        }
      };
      
      updateLoanAmount(client.cid, newAmount);
      setClient(updatedClient);
      setShowEditModal(false);
      setSuccess('Loan amount updated successfully');
    }
  };

  const calculateCreditScore = () => {
    const weights = {
      paymentHistory: 0.35,
      creditUtilization: 0.30,
      creditHistoryLength: 0.15,
      creditMix: 0.10,
      newInquiries: 0.10
    };

    const input = client.input;
    const score = 
      (input.paymentHistory * weights.paymentHistory) +
      (input.creditUtilization * weights.creditUtilization) +
      (input.creditHistoryLength * weights.creditHistoryLength) +
      (input.creditMix * weights.creditMix) +
      (input.newInquiries * weights.newInquiries);
    
    return Math.round(score);
  };

  const getCreditRating = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 70) return 'Good';
    if (score >= 60) return 'Fair';
    if (score >= 50) return 'Poor';
    return 'Very Poor';
  };

  return (
    <ScrollView style={styles.container}>
      {/* Topbar */}
      <View style={styles.topbar}>
        <Text style={styles.logo}><Text style={{ color: '#0066ff' }}>Loan</Text>Siya</Text>
        <Text style={styles.role}>Operations Manager</Text>
    
      </View>

      {/* Body */}
      <View style={styles.content}>
        {/* LEFT SIDE */}
        <View style={styles.left}>
          <Text style={styles.sectionTitle}>Client Profile</Text>
          <Text style={styles.subTitle}>Personal Details</Text>
          <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
          <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
          <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
          <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
          <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
          <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>

          <Text style={[styles.subTitle, { marginTop: 16 }]}>Loan History</Text>
          {client.loans.loanHistory.length > 0 ? (
            client.loans.loanHistory.map((loan, i) => (
              <View key={i} style={styles.loanHistoryRow}>
                <Text>Amount: ₱{loan.amount.toLocaleString()}</Text>
                <Text>Purpose: {loan.purpose}</Text>
                <Text>Date: {loan.dateApplied}</Text>
                <Text>Status: {loan.status}</Text>
                <Text>Paid: {loan.paid ? 'Yes' : 'No'}</Text>
              </View>
            ))
          ) : (
            <Text>No loan history</Text>
          )}
        </View>

        {/* RIGHT SIDE */}
        <View style={styles.right}>
          <Text style={styles.sectionTitle}>Financial Record</Text>
          <Text>Monthly Income: ₱{client.financial.monthlyIncome.toLocaleString()}</Text>
          <Text>Monthly Expenses: ₱{client.financial.monthlyExpenses.toLocaleString()}</Text>
          <Text>Assets: ₱{client.financial.assets.toLocaleString()}</Text>
          <Text>Debt to Income Ratio: {client.financial.dti}%</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Employment Info</Text>
          <Text>Employer: {client.employer}</Text>
          <Text>Position: {client.position}</Text>

          {/* Credit Assessment */}
          <View style={styles.creditBox}>
            <Text style={styles.bold}>Credit Score</Text>
            <Text style={styles.score}>{calculateCreditScore()}</Text>
            <Text style={styles.rating}>{getCreditRating(calculateCreditScore())}</Text>
            
            <Text style={[styles.bold, { marginTop: 10 }]}>Risk Assessment</Text>
            <Text>Payment History Score: {client.input.paymentHistory}%</Text>
            <Text>Credit Utilization: {client.input.creditUtilization}%</Text>
            <Text>Credit History Length Score: {client.input.creditHistoryLength}%</Text>
            <Text>Credit Mix Score: {client.input.creditMix}%</Text>
            <Text>New Inquiries Score: {client.input.newInquiries}%</Text>
          </View>

          {/* Current Request */}
          <View style={styles.loanBox}>
            <Text style={styles.bold}>Current Balance</Text>
            <Text>Amount: ₱{client.loanBalance.amount.toLocaleString()}</Text>
            <Text>Due Date: {client.loanBalance.dueDate}</Text>

            <Text style={[styles.bold, { marginTop: 10 }]}>Latest Request</Text>
            {client.loans.loanHistory.length > 0 ? (
              (() => {
                const latest = [...client.loans.loanHistory].sort((a, b) => 
                  new Date(b.dateApplied) - new Date(a.dateApplied)
                )[0];
                return (
                  <>
                    <Text>Amount: ₱{latest.amount.toLocaleString()}</Text>
                    <Text>Purpose: {latest.purpose}</Text>
                    <Text>Date Applied: {latest.dateApplied}</Text>
                  </>
                );
              })()
            ) : (
              <Text>No pending requests</Text>
            )}
          </View>

          {/* Action Buttons */}
          <View style={styles.actionRow}>
            <TouchableOpacity style={styles.backBtn} onPress={() => navigation.goBack()}>
              <Text>← Back</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.editBtn}
              onPress={() => {
                const latestRequest = client.loans.loanHistory
                  .sort((a, b) => new Date(b.dateApplied) - new Date(a.dateApplied))[0];
                setEditedAmount(latestRequest?.amount.toString() || '0');
                setShowEditModal(true);
              }}
            >
              <Text>Edit</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.declineBtn}
              onPress={handleDecline}
            >
              <Text style={{ color: '#fff' }}>Decline</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.approveBtn}
              onPress={handleApprove}
            >
              <Text style={{ color: '#fff' }}>Approve</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>

      {/* Edit Modal */}
      <Modal
        visible={showEditModal}
        transparent={true}
        animationType="fade"
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Edit Loan Amount</Text>
            {client.loans.loanHistory.length > 0 && (
              <Text style={styles.modalSubtitle}>
                Maximum amount: ₱{
                  client.loans.loanHistory
                    .sort((a, b) => new Date(b.dateApplied) - new Date(a.dateApplied))[0]
                    .amount.toLocaleString()
                }
              </Text>
            )}
            
            <TextInput
              style={styles.amountInput}
              value={editedAmount}
              onChangeText={(text) => {
                setEditedAmount(text.replace(/[^0-9]/g, ''));
                setError('');
              }}
              keyboardType="numeric"
              placeholder="Enter amount"
            />
            
            {error ? <Text style={styles.errorText}>{error}</Text> : null}
            
            <View style={styles.modalButtons}>
              <TouchableOpacity
                style={styles.cancelBtn}
                onPress={() => {
                  setShowEditModal(false);
                  setError('');
                }}
              >
                <Text>Cancel</Text>
              </TouchableOpacity>
              
              <TouchableOpacity
                style={styles.saveBtn}
                onPress={handleSaveEdit}
              >
                <Text style={{ color: '#fff' }}>Save Changes</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>

      {/* Success Message */}
      {success ? (
        <View style={styles.successMessage}>
          <Text style={styles.successText}>{success}</Text>
        </View>
      ) : null}
    </ScrollView>
  );
}

