import React, { useState } from 'react';
import { View, Text, TextInput, FlatList, TouchableOpacity, StyleSheet, ActivityIndicator, useWindowDimensions, Platform } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';

export default function OpsClientListScreen({ navigation }) {
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  
  const { loans, loading, error } = useLoan();
  const [search, setSearch] = useState('');

  const filtered = loans.filter(
    item =>
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.cid.includes(search)
  );

  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={[styles.row, isMobile && styles.rowMobile]}
      onPress={() => {
        navigation.navigate('OpsClientProfile', {
          client: item
        });
      }}
    >
      {isMobile ? (
        // Mobile layout
        <View style={styles.mobileContent}>
          <Text style={styles.mobileName}>{item.name}</Text>
          <Text style={styles.mobileCid}>CID: {item.cid}</Text>
        </View>
      ) : (
        // Desktop layout
        <>
          <Text style={[styles.cell, { flex: 1 }]}>{item.cid}</Text>
          <Text style={[styles.cell, { flex: 2 }]}>{item.name}</Text>
          <TouchableOpacity
            style={styles.viewBtn}
          >
            <Text style={styles.viewBtnText}>View</Text>
          </TouchableOpacity>
        </>
      )}
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <ActivityIndicator size="large" color="#0066ff" />
        <Text style={styles.loadingText}>Loading clients...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>{error}</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      {/* Header Row */}
      <View style={styles.headerRow}>
        <Text style={[styles.title, isMobile && styles.titleMobile]}>Client List</Text>
        <TouchableOpacity
          style={[styles.dashboardBtn, isMobile && styles.dashboardBtnMobile]}
          onPress={() => navigation.replace('OpsDashboard')}
        >
          <Text style={[styles.btnText, isMobile && styles.btnTextMobile]}>Go to Dashboard</Text>
        </TouchableOpacity>
      </View>

      {/* Search */}
      <View style={[styles.searchBar, isMobile && styles.searchBarMobile]}>
        <TextInput
          style={[styles.searchInput, isMobile && styles.searchInputMobile]}
          placeholder="Search by name or CID"
          value={search}
          onChangeText={setSearch}
        />
      </View>

      {/* Table Header - Only show on desktop */}
      {!isMobile && (
        <View style={styles.tableHeader}>
          <Text style={[styles.headerCell, { flex: 1 }]}>CID</Text>
          <Text style={[styles.headerCell, { flex: 2 }]}>Full Name</Text>
          <Text style={[styles.headerCell, { flex: 1 }]}></Text>
        </View>
      )}

      <FlatList
        data={filtered}
        keyExtractor={(item) => item.cid}
        renderItem={renderItem}
        ListEmptyComponent={() => (
          <View style={styles.emptyContainer}>
            <Text style={styles.emptyText}>
              {search ? 'No clients match your search' : 'No clients found'}
            </Text>
          </View>
        )}
        contentContainerStyle={styles.listContainer}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: Platform.select({ web: 20, default: 16 }),
    backgroundColor: '#f6f6f6',
    flex: 1,
  },
  centerContent: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  headerRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: Platform.select({ web: 20, default: 16 }),
  },
  title: {
    fontSize: Platform.select({ web: 22, default: 20 }),
    fontWeight: 'bold',
  },
  titleMobile: {
    fontSize: 18,
  },
  dashboardBtn: {
    backgroundColor: '#007bff',
    paddingVertical: Platform.select({ web: 10, default: 8 }),
    paddingHorizontal: Platform.select({ web: 16, default: 12 }),
    borderRadius: 8,
  },
  dashboardBtnMobile: {
    paddingVertical: 8,
    paddingHorizontal: 10,
  },
  btnText: {
    color: '#fff',
    fontWeight: '600',
    fontSize: Platform.select({ web: 16, default: 14 }),
  },
  btnTextMobile: {
    fontSize: 12,
  },
  searchBar: {
    flexDirection: 'row',
    marginBottom: Platform.select({ web: 16, default: 12 }),
  },
  searchBarMobile: {
    marginBottom: 10,
  },
  searchInput: {
    backgroundColor: '#fff',
    borderRadius: 8,
    padding: Platform.select({ web: 12, default: 10 }),
    flex: 1,
    fontSize: Platform.select({ web: 16, default: 14 }),
    borderWidth: 1,
    borderColor: '#ddd',
  },
  searchInputMobile: {
    padding: 8,
    fontSize: 14,
  },
  tableHeader: {
    flexDirection: 'row',
    backgroundColor: '#e9ecef',
    paddingVertical: 12,
    paddingHorizontal: 8,
    marginBottom: 4,
    borderRadius: 8,
  },
  headerCell: {
    fontWeight: 'bold',
    fontSize: 16,
    color: '#495057',
  },
  row: {
    flexDirection: 'row',
    backgroundColor: '#fff',
    paddingVertical: Platform.select({ web: 12, default: 10 }),
    paddingHorizontal: Platform.select({ web: 8, default: 6 }),
    marginBottom: 8,
    borderRadius: 8,
    alignItems: 'center',
    borderWidth: 1,
    borderColor: '#e9ecef',
  },
  rowMobile: {
    padding: 12,
  },
  cell: {
    fontSize: Platform.select({ web: 16, default: 14 }),
    paddingHorizontal: 6,
    color: '#212529',
  },
  mobileContent: {
    flex: 1,
  },
  mobileName: {
    fontSize: 16,
    fontWeight: '500',
    marginBottom: 4,
    color: '#212529',
  },
  mobileCid: {
    fontSize: 14,
    color: '#6c757d',
  },
  viewBtn: {
    backgroundColor: '#e9ecef',
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderRadius: 6,
  },
  viewBtnText: {
    color: '#495057',
    fontSize: 14,
    fontWeight: '500',
  },
  emptyContainer: {
    padding: Platform.select({ web: 20, default: 16 }),
    alignItems: 'center',
  },
  emptyText: {
    fontSize: Platform.select({ web: 16, default: 14 }),
    color: '#6c757d',
  },
  loadingText: {
    marginTop: 10,
    fontSize: Platform.select({ web: 16, default: 14 }),
    color: '#6c757d',
  },
  errorText: {
    color: '#dc3545',
    fontSize: Platform.select({ web: 16, default: 14 }),
    marginBottom: 15,
    textAlign: 'center',
  },
  listContainer: {
    flexGrow: 1,
  },
});
