import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, ActivityIndicator, Modal } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';
import { API_BASE_URL, clientAPI, DOCUMENT_CATEGORIES } from '../../services/api';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  documents: {
    applicationDocs: DOCUMENT_CATEGORIES.APPLICATION_DOCS,
    signedDocuments: []
  },
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A'
};

// Initial client state
const initialClientState = {
  ...defaultClient,
  documents: {
    applicationDocs: [],
    signedDocuments: []
  }
};

export default function OpsClientProfileScreen({ navigation, route }) {
  const [client, setClient] = useState(initialClientState);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showDocumentModal, setShowDocumentModal] = useState(false);
  const [modalContent, setModalContent] = useState(null);
  const { refreshClientData } = useLoan();
  const cid = route.params?.client?.cid;

  useEffect(() => {
    if (cid) {
      loadClientData();
    }
  }, [cid]);

  const loadClientData = async () => {
    try {
      setLoading(true);
      setError(null);
      const clientData = await refreshClientData(cid);
      
      // Ensure we have the correct document structure
      const documents = {
        applicationDocs: clientData.documents?.applicationDocs || defaultClient.documents.applicationDocs,
        signedDocuments: clientData.documents?.signedDocuments || []
      };

      setClient({
        ...defaultClient,
        ...clientData,
        documents
      });
    } catch (err) {
      console.error('Error loading client:', err);
      setError('Failed to load client data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <ActivityIndicator size="large" color="#0066ff" />
        <Text style={styles.loadingText}>Loading client data...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryBtn} onPress={loadClientData}>
          <Text style={styles.retryText}>Retry</Text>
        </TouchableOpacity>
      </View>
    );
  }


  return (
    <ScrollView style={styles.container}>
      {/* Topbar */}
      <View style={styles.topbar}>
        <Text style={styles.logo}><Text style={{ color: '#0066ff' }}>Loan</Text>Siya</Text>
        <Text style={styles.role}>Operations Manager</Text>
      </View>

      {/* Body */}
      <View style={styles.content}>
        {/* LEFT SIDE */}
        <View style={styles.left}>
          <Text style={styles.sectionTitle}>Client Profile</Text>
          <Text style={styles.subTitle}>Personal Details</Text>
          <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
          <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
          <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
          <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
          <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
          <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>

          <Text style={[styles.subTitle, { marginTop: 16 }]}>Uploaded Documents</Text>
          
          <View style={styles.documentCategory}>
            <Text style={styles.categoryText}>Application Documents</Text>
            <TouchableOpacity
              style={styles.previewBtn}
              onPress={async () => {
                try {
                  // Show modal with all application documents
                  setModalContent({
                    title: 'Application Documents',
                    documents: client?.documents?.applicationDocs || []
                  });
                  setShowDocumentModal(true);
                } catch (error) {
                  console.error('Error showing documents:', error);
                  alert('Error loading documents. Please try again.');
                }
              }}
            >
              <Text style={styles.previewText}>View</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.documentCategory}>
            <Text style={styles.categoryText}>Signed Documents</Text>
            <TouchableOpacity
              style={styles.previewBtn}
              onPress={async () => {
                if (client?.documents?.signedDocuments?.length > 0) {
                  // Show modal with signed documents
                  setModalContent({
                    title: 'Signed Documents',
                    documents: client.documents.signedDocuments
                  });
                  setShowDocumentModal(true);
                } else {
                  alert('No signed documents available yet. Documents will be available after loan approval.');
                }
              }}
            >
              <Text style={styles.previewText}>View</Text>
            </TouchableOpacity>
          </View>

          {/* Document Viewer Modal */}
          <Modal
            visible={showDocumentModal}
            transparent={true}
            animationType="fade"
            onRequestClose={() => setShowDocumentModal(false)}
          >
            <View style={styles.modalOverlay}>
              <View style={styles.modalContent}>
                <Text style={styles.modalTitle}>{modalContent?.title}</Text>
                {modalContent?.documents?.map((doc, i) => (
                  <TouchableOpacity
                    key={i}
                    style={styles.documentButton}
                    onPress={async () => {
                      try {
                        const { url, type } = await clientAPI.viewDocument(
                          client.cid,
                          typeof doc === 'string' ? doc : doc.name
                        );
                        window.open(url, '_blank');
                        setTimeout(() => URL.revokeObjectURL(url), 1000);
                      } catch (error) {
                        console.error('Error viewing document:', error);
                        alert('Error viewing document. Please try again.');
                      }
                    }}
                  >
                    <Text style={styles.documentButtonText}>
                      {typeof doc === 'string' ? doc : doc.name}
                    </Text>
                  </TouchableOpacity>
                ))}
                <TouchableOpacity
                  style={styles.closeButton}
                  onPress={() => setShowDocumentModal(false)}
                >
                  <Text style={styles.closeButtonText}>Close</Text>
                </TouchableOpacity>
              </View>
            </View>
          </Modal>
        </View>

        {/* RIGHT SIDE */}
        <View style={styles.right}>
          <Text style={styles.sectionTitle}>Financial Record</Text>
          <Text>Monthly Income ₱{client.financial.monthlyIncome.toLocaleString()}</Text>
          <Text>Monthly Expenses ₱{client.financial.monthlyExpenses.toLocaleString()}</Text>
          <Text>Saving and Assets ₱{client.financial.assets.toLocaleString()}</Text>
          <Text>Debt to Income Ratio {client.financial.dti}%</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Employment or Business info</Text>
          <Text>{client.employer}</Text>
          <Text>{client.position}</Text>


          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Loan History</Text>
          <TouchableOpacity
            style={styles.viewBtn}
            onPress={() => navigation.navigate('LoanHistory', {
              clientId: client.cid,
              userRole: 'ops'
            })}
          >
            <Text>View</Text>
          </TouchableOpacity>

          <View style={styles.actionRow}>
            <TouchableOpacity 
              style={styles.backBtn} 
              onPress={() => navigation.goBack()}
            >
              <Text>← Back to List</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.refreshBtn} 
              onPress={loadClientData}
            >
              <Text>↻ Refresh</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
     
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  documentCategory: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#f5f5f5',
    padding: 16,
    marginBottom: 10,
    borderRadius: 6,
    borderWidth: 1,
    borderColor: '#e0e0e0',
  },
  categoryText: {
    fontSize: 15,
    color: '#333',
    fontWeight: '500',
  },
  modalOverlay: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalContent: {
    backgroundColor: 'white',
    padding: 20,
    borderRadius: 8,
    width: '80%',
    maxWidth: 400,
  },
  modalTitle: {
    fontSize: 18,
