import React from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';

const mockNotifications = [
  { cid: '001', status: 'approved' },
  { cid: '002', status: 'declined' },
  { cid: '003', status: 'declined' },
  { cid: '004', status: 'approved' },
  { cid: '005', status: 'approved' },
];

export default function LoanOfficerNotifications() {
  const renderItem = ({ item }) => {
    const isApproved = item.status === 'approved';

    return (
      <View style={styles.item}>
        <Text style={[styles.icon, { color: isApproved ? 'green' : 'red' }]}>
          {isApproved ? '✅' : '❌'}
        </Text>
        <Text style={styles.message}>
          CID <Text style={styles.bold}>{item.cid}</Text> LOAN REQUEST HAS BEEN{' '}
          <Text style={[styles.status, { color: isApproved ? 'green' : 'red' }]}>
            {item.status.toUpperCase()}
          </Text>{' '}
          BY THE OPERATIONAL MANAGER
        </Text>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Hello Loan Officer!</Text>
      <Text style={styles.subHeader}>Notifications</Text>

      <FlatList
        data={mockNotifications}
        keyExtractor={(item) => item.cid}
        renderItem={renderItem}
        ItemSeparatorComponent={() => <View style={styles.separator} />}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 24,
    backgroundColor: '#f6f6f6',
    flex: 1,
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    alignSelf: 'center',
    marginBottom: 16,
  },
  subHeader: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 16,
  },
  item: {
    flexDirection: 'row',
    alignItems: 'flex-start',
    paddingVertical: 6,
  },
  icon: {
    fontSize: 18,
    marginRight: 10,
    marginTop: 2,
  },
  message: {
    flex: 1,
    fontSize: 14,
    fontWeight: '500',
  },
  bold: {
    fontWeight: 'bold',
  },
  status: {
    fontWeight: 'bold',
    textTransform: 'uppercase',
  },
  separator: {
    borderBottomWidth: 1,
    borderColor: '#ccc',
    marginVertical: 6,
  },
});
