import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet, ActivityIndicator, Modal, useWindowDimensions, Platform } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';
import { API_BASE_URL, clientAPI, DOCUMENT_CATEGORIES } from '../../services/api';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  documents: {
    applicationDocs: DOCUMENT_CATEGORIES.APPLICATION_DOCS,
    signedDocuments: []
  },
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A'
};

const initialClientState = {
  ...defaultClient,
  documents: {
    applicationDocs: [],
    signedDocuments: []
  }
};

export default function OpsClientProfileScreen({ navigation, route }) {
  const { width } = useWindowDimensions();
  const isMobile = width < 768;
  const isTablet = width >= 768 && width < 1024;
  
  const [client, setClient] = useState(initialClientState);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showDateModal, setShowDateModal] = useState(false);
  const [documentDates, setDocumentDates] = useState([]);
  const [loadingDates, setLoadingDates] = useState(false);
  const [modalType, setModalType] = useState('application');
  const { refreshClientData } = useLoan();
  const cid = route.params?.client?.cid;

  // ... keep all the existing functions (loadClientData, loadDocumentDates, etc.) ...

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <ActivityIndicator size="large" color="#0066ff" />
        <Text style={styles.loadingText}>Loading client data...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryBtn} onPress={loadClientData}>
          <Text style={styles.retryText}>Retry</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={[styles.content, isMobile && styles.contentMobile]}>
        {/* LEFT SIDE */}
        <View style={[styles.section, isMobile && styles.sectionMobile]}>
          <Text style={styles.sectionTitle}>Client Profile</Text>
          <Text style={styles.subTitle}>Personal Details</Text>
          <View style={styles.detailsContainer}>
            <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
            <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
            <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
            <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
            <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
            <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>
          </View>

          <Text style={[styles.subTitle, { marginTop: 16 }]}>Uploaded Documents</Text>
          
          <View style={styles.documentCategory}>
            <Text style={styles.categoryText}>Application Documents</Text>
            <TouchableOpacity
              style={styles.previewBtn}
              onPress={loadDocumentDates}
              disabled={loadingDates}
            >
              <Text style={styles.previewText}>
                {loadingDates ? 'Loading...' : 'View'}
              </Text>
            </TouchableOpacity>
          </View>

          <View style={styles.documentCategory}>
            <Text style={styles.categoryText}>Signed Documents</Text>
            <TouchableOpacity
              style={styles.previewBtn}
              onPress={loadSignedDocumentDates}
              disabled={loadingDates}
            >
              <Text style={styles.previewText}>
                {loadingDates ? 'Loading...' : 'View'}
              </Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* RIGHT SIDE */}
        <View style={[styles.section, isMobile && styles.sectionMobile]}>
          <Text style={styles.sectionTitle}>Financial Record</Text>
          <View style={styles.detailsContainer}>
            <Text>Monthly Income ₱{client.financial.monthlyIncome.toLocaleString()}</Text>
            <Text>Monthly Expenses ₱{client.financial.monthlyExpenses.toLocaleString()}</Text>
            <Text>Saving and Assets ₱{client.financial.assets.toLocaleString()}</Text>
            <Text>Debt to Income Ratio {client.financial.dti}%</Text>
          </View>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Employment or Business info</Text>
          <View style={styles.detailsContainer}>
            <Text>{client.employer}</Text>
            <Text>{client.position}</Text>
          </View>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Loan History</Text>
          <TouchableOpacity
            style={styles.viewBtn}
            onPress={() => navigation.navigate('LoanHistory', {
              clientId: client.cid,
              userRole: 'ops'
            })}
          >
            <Text style={styles.viewBtnText}>View History</Text>
          </TouchableOpacity>

          <View style={styles.actionRow}>
            <TouchableOpacity 
              style={styles.backBtn} 
              onPress={() => navigation.goBack()}
            >
              <Text style={styles.actionBtnText}>← Back to List</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.refreshBtn} 
              onPress={loadClientData}
            >
              <Text style={styles.actionBtnText}>↻ Refresh</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>

      {/* Date Selection Modal */}
      <Modal
        visible={showDateModal}
        transparent={true}
        animationType="fade"
        onRequestClose={() => setShowDateModal(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={[styles.modalContent, isMobile && styles.modalContentMobile]}>
            <Text style={styles.modalTitle}>
              Select {modalType === 'application' ? 'Application' : 'Signed'} Document Date
            </Text>
            
            <ScrollView style={styles.dateList}>
              {documentDates.length > 0 ? (
                documentDates.map((dateInfo, index) => (
                  <TouchableOpacity
                    key={index}
                    style={styles.dateButton}
                    onPress={() => {
                      if (modalType === 'application') {
                        handleDateSelection(dateInfo.date);
                      } else {
                        handleSignedDateSelection(dateInfo.date);
                      }
                    }}
                  >
                    <Text style={styles.dateButtonText}>
                      📅 {formatDateForDisplay(dateInfo.date)}
                    </Text>
                    <Text style={styles.documentCountText}>
                      ({dateInfo.documentCount} documents)
                    </Text>
                  </TouchableOpacity>
                ))
              ) : (
                <Text style={styles.noDataText}>No document dates found</Text>
              )}
            </ScrollView>
            
            <TouchableOpacity
              style={styles.closeButton}
              onPress={() => setShowDateModal(false)}
            >
              <Text style={styles.closeButtonText}>Cancel</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { 
    backgroundColor: '#f6f6f6',
    flex: 1,
  },
  content: {
    flexDirection: 'row',
    padding: Platform.select({ web: 20, default: 16 }),
  },
  contentMobile: {
    flexDirection: 'column',
  },
  section: {
    flex: 1,
    padding: Platform.select({ web: 20, default: 16 }),
  },
  sectionMobile: {
    padding: 12,
    marginBottom: 16,
  },
  detailsContainer: {
    backgroundColor: '#fff',
    padding: Platform.select({ web: 16, default: 12 }),
    borderRadius: 8,
    marginTop: 8,
    gap: 8,
  },
  centerContent: {
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
