import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, ScrollView, ActivityIndicator, Modal } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';
import { API_BASE_URL, clientAPI, DOCUMENT_CATEGORIES } from '../../services/api';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  documents: {
    applicationDocs: DOCUMENT_CATEGORIES.APPLICATION_DOCS,
    signedDocuments: []
  },
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A'
};

export default function ClientProfileScreen({ route, navigation }) {
  const [client, setClient] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showDateModal, setShowDateModal] = useState(false);
  const [documentDates, setDocumentDates] = useState([]);
  const [loadingDates, setLoadingDates] = useState(false);
  const [modalType, setModalType] = useState('application');
  const { refreshClientData } = useLoan();
  const cid = route.params?.client?.cid;

  useEffect(() => {
    if (cid) {
      loadClientData();
    }
  }, [cid]);

  const loadClientData = async () => {
    try {
      setLoading(true);
      setError(null);
      const clientData = await refreshClientData(cid);
      // Ensure we have the correct document structure
      const documents = {
        applicationDocs: clientData.documents?.applicationDocs || defaultClient.documents.applicationDocs,
        signedDocuments: clientData.documents?.signedDocuments || []
      };

      setClient({
        ...defaultClient,
        ...clientData,
        documents
      });
    } catch (err) {
      console.error('Error loading client:', err);
      setError('Failed to load client data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const loadDocumentDates = async () => {
    try {
      setLoadingDates(true);
      setModalType('application');
      const dates = await clientAPI.getDocumentDates(cid);
      
      // Filter out dates where only PDFs or promissory notes exist
      const filteredDates = [];
      for (const dateInfo of dates) {
        const documents = await clientAPI.getDocumentsByDate(cid, dateInfo.date);
        const applicationDocs = documents.filter(doc =>
          !['pdf', 'promissory-note'].some(keyword =>
            doc.type.includes(keyword) ||
            doc.name.toLowerCase().includes(keyword) ||
            doc.displayName.toLowerCase().includes(keyword)
          )
        );
      
        if (applicationDocs.length > 0) {
          filteredDates.push({
            ...dateInfo,
            documentCount: applicationDocs.length
          });
        }
      }
      setDocumentDates(filteredDates);
      setShowDateModal(true);
    } catch (error) {
      console.error('Error loading document dates:', error);
      alert('Error loading document dates. Please try again.');
    } finally {
      setLoadingDates(false);
    }
  };

  const handleDateSelection = async (selectedDate) => {
    try {
      setShowDateModal(false);
      const documents = await clientAPI.getDocumentsByDate(client.cid, selectedDate);
      if (documents.length > 0) {
        // Filter out promissory notes from Application Documents
        const applicationDocs = documents.filter(doc =>
          !['pdf', 'promissory-note'].some(keyword =>
            doc.type.includes(keyword) ||
            doc.name.toLowerCase().includes(keyword) ||
            doc.displayName.toLowerCase().includes(keyword)
          )
        );
        
        navigation.navigate('DocumentPreview', {
          cid: client.cid,
          title: `Application Documents - ${formatDateForDisplay(selectedDate)}`,
          documents: applicationDocs,
          latestDate: selectedDate
        });
      } else {
        alert('No application documents found for the selected date');
      }
    } catch (error) {
      console.error('Error loading documents:', error);
      alert('Error loading documents. Please try again.');
    }
  };

  const handleSignedDateSelection = async (selectedDate) => {
    try {
      setShowDateModal(false);
      const documents = await clientAPI.getDocumentsByDate(client.cid, selectedDate);
      if (documents.length > 0) {
        // Filter to only show PDF documents (signed documents like promissory notes)
        const signedDocs = documents.filter(doc =>
          ['pdf', 'promissory-note'].some(keyword =>
            doc.type.includes(keyword) ||
            doc.name.toLowerCase().includes(keyword) ||
            doc.displayName.toLowerCase().includes(keyword)
          )
        );
        
        if (signedDocs.length > 0) {
          navigation.navigate('DocumentPreview', {
            cid: client.cid,
            title: `Signed Documents - ${formatDateForDisplay(selectedDate)}`,
            documents: signedDocs,
            latestDate: selectedDate
          });
        } else {
          alert('No signed documents found for the selected date');
        }
      } else {
        alert('No documents found for the selected date');
      }
    } catch (error) {
      console.error('Error loading signed documents:', error);
      alert('Error loading signed documents. Please try again.');
    }
  };

  const loadSignedDocumentDates = async () => {
    try {
      setLoadingDates(true);
      setModalType('signed');
      const dates = await clientAPI.getDocumentDates(cid);
      // Filter to only show dates that have PDF documents
      const datesWithSignedDocs = [];
      for (const dateInfo of dates) {
        const documents = await clientAPI.getDocumentsByDate(cid, dateInfo.date);
        const signedDocs = documents.filter(doc =>
          doc.type.includes('pdf') ||
          doc.name.toLowerCase().includes('promissory-note') ||
          doc.displayName.toLowerCase().includes('promissory note')
        );
        if (signedDocs.length > 0) {
          datesWithSignedDocs.push({
            ...dateInfo,
            documentCount: signedDocs.length
          });
        }
      }
      setDocumentDates(datesWithSignedDocs);
      setShowDateModal(true);
    } catch (error) {
      console.error('Error loading signed document dates:', error);
      alert('Error loading signed document dates. Please try again.');
    } finally {
      setLoadingDates(false);
    }
  };

  const formatDateForDisplay = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <ActivityIndicator size="large" color="#0066ff" />
        <Text style={styles.loadingText}>Loading client data...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryBtn} onPress={loadClientData}>
          <Text style={styles.retryText}>Retry</Text>
        </TouchableOpacity>
      </View>
    );
  }

  if (!client) {
    return (
      <View style={[styles.container, styles.centerContent]}>
        <Text style={styles.errorText}>Client not found</Text>
        <TouchableOpacity 
          style={styles.backBtn} 
          onPress={() => navigation.goBack()}
        >
          <Text>‚Üê Back to List</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <ScrollView style={styles.container}>
      {/* Topbar */}
      <View style={styles.topbar}>
        <View style={styles.leftSection}>
          <Text style={styles.logo}><Text style={{ color: '#0066ff' }}>Loan</Text>Siya</Text>
        </View>
        <View style={styles.rightSection}>
          <Text style={styles.role}>Loan Officer</Text>
          <TouchableOpacity onPress={() => navigation.replace('Login')}>
            <Text style={styles.logout}>Logout</Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Content */}
      <View style={styles.content}>
        {/* Left side */}
        <View style={styles.left}>
          <Text style={styles.sectionTitle}>Client Profile</Text>

          <Text style={styles.subTitle}>Personal Details</Text>
          <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
          <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
          <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
          <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
          <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
          <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 20 }]}>Uploaded Documents</Text>
          
          <View style={styles.documentCategory}>
            <Text style={styles.categoryText}>Application Documents</Text>
            <TouchableOpacity
              style={styles.previewBtn}
              onPress={loadDocumentDates}
              disabled={loadingDates}
            >
              <Text style={styles.previewText}>
                {loadingDates ? 'Loading...' : 'View'}
              </Text>
            </TouchableOpacity>
          </View>

          <View style={styles.documentCategory}>
            <Text style={styles.categoryText}>Signed Documents</Text>
            <TouchableOpacity
              style={styles.previewBtn}
              onPress={loadSignedDocumentDates}
              disabled={loadingDates}
            >
              <Text style={styles.previewText}>
                {loadingDates ? 'Loading...' : 'View'}
              </Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* Right side */}
        <View style={styles.right}>
          <Text style={styles.sectionTitle}>Financial Record</Text>
          <Text>Monthly Income ‚Ç±{client.financial.monthlyIncome.toLocaleString()}</Text>
          <Text>Monthly Expenses ‚Ç±{client.financial.monthlyExpenses.toLocaleString()}</Text>
          <Text>Saving and Assets ‚Ç±{client.financial.assets.toLocaleString()}</Text>
          <Text>Debt to Income Ratio {client.financial.dti}%</Text>

          <Text style={[styles.sectionTitle, { marginTop: 20 }]}>Employment or Business info</Text>
          <Text>{client.employer}</Text>
          <Text>{client.position}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 20 }]}>Loan History</Text>
          <TouchableOpacity
            style={styles.viewBtn}
            onPress={() => navigation.navigate('LoanHistory', {
              clientId: client.cid,
              userRole: 'loanofficer'
            })}
          >
            <Text>View</Text>
          </TouchableOpacity>

          <View style={styles.actionRow}>
            <TouchableOpacity 
              style={styles.backBtn} 
              onPress={() => navigation.goBack()}
            >
              <Text>‚Üê Back</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={styles.refreshBtn} 
              onPress={loadClientData}
            >
              <Text>‚Üª Refresh</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
     {/* Date Selection Modal */}
     <Modal
       visible={showDateModal}
       transparent={true}
       animationType="fade"
       onRequestClose={() => setShowDateModal(false)}
     >
       <View style={styles.modalOverlay}>
         <View style={styles.modalContent}>
           <Text style={styles.modalTitle}>
             Select {modalType === 'application' ? 'Application' : 'Signed'} Document Date
           </Text>
           
           {documentDates.length > 0 ? (
             documentDates.map((dateInfo, index) => (
               <TouchableOpacity
                 key={index}
                 style={styles.dateButton}
                 onPress={() => {
                   if (modalType === 'application') {
                     handleDateSelection(dateInfo.date);
                   } else {
                     handleSignedDateSelection(dateInfo.date);
                   }
                 }}
               >
                 <Text style={styles.dateButtonText}>
                   üìÖ {formatDateForDisplay(dateInfo.date)}
                 </Text>
                 <Text style={styles.documentCountText}>
                   ({dateInfo.documentCount} documents)
                 </Text>
               </TouchableOpacity>
             ))
           ) : (
             <Text style={styles.noDataText}>No document dates found</Text>
           )}
           
           <TouchableOpacity
             style={styles.closeButton}
             onPress={() => setShowDateModal(false)}
           >
             <Text style={styles.closeButtonText}>Cancel</Text>
           </TouchableOpacity>
         </View>
       </View>
     </Modal>

    </ScrollView>
  );
}

