import React, { createContext, useState } from 'react';

export const UsersContext = createContext({
  users: [],
  addUser: (user) => {},
  updateUser: (user) => {},
});

export const UsersProvider = ({ children }) => {
  const [users, setUsers] = useState([]);

  const addUser = (user) => {
    // Check if user with same username exists
    const existingUsername = users.find(u => u.username.toLowerCase() === user.username.toLowerCase());
    if (existingUsername) {
      throw new Error('Username already exists');
      return;
    }

    // Check if user with same name and role exists
    const existingNameAndRole = users.find(
      u => u.fullName.toLowerCase() === user.fullName.toLowerCase() &&
          u.role === user.role
    );
    if (existingNameAndRole) {
      throw new Error('A user with this name and role already exists');
      return;
    }

    setUsers([...users, {
      ...user,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    }]);
  };

  const updateUser = (updatedUser) => {
    if (!updatedUser.id) {
      throw new Error('User ID is required for updating');
    }
    
    // Find the target user by ID
    const targetUser = users.find(u => u.id === updatedUser.id);
    if (!targetUser) {
      throw new Error('User not found');
    }

    // Only check for username duplicates if username is being changed
    if (updatedUser.username && targetUser.username !== updatedUser.username) {
      const existingUser = users.find(u =>
        u.id !== updatedUser.id &&
        u.username.toLowerCase() === updatedUser.username.toLowerCase()
      );
      if (existingUser) {
        throw new Error('Username already exists');
      }
    }

    // Check for duplicate name and role if those fields are being changed
    if ((updatedUser.fullName && targetUser.fullName !== updatedUser.fullName) ||
        (updatedUser.role && targetUser.role !== updatedUser.role)) {
      const existingNameAndRole = users.find(u =>
        u.id !== updatedUser.id &&
        u.fullName.toLowerCase() === (updatedUser.fullName || targetUser.fullName).toLowerCase() &&
        u.role === (updatedUser.role || targetUser.role)
      );
      if (existingNameAndRole) {
        throw new Error('A user with this name and role already exists');
      }
    }

    // Update the specific user only
    setUsers(users.map(user => {
      if (user.id === updatedUser.id) {
        return {
          ...user,
          ...updatedUser,
          updatedAt: new Date().toISOString()
        };
      }
      return user;
    }));
  };

  return (
    <UsersContext.Provider value={{
      users,
      addUser,
      updateUser,
    }}>
      {children}
    </UsersContext.Provider>
  );
};
