export function LoanProvider({ children }) {
  const [loans, setLoans] = useState(initialLoans);
  const [notifications, setNotifications] = useState(initialNotifications);
  const [unreadCount, setUnreadCount] = useState(0);

  const addNotification = (notification) => {
    setNotifications(prev => [{
      id: Date.now(),
      timestamp: new Date().toISOString(),
      read: false,
      ...notification
    }, ...prev]);
    setUnreadCount(prev => prev + 1);
  };

  const markNotificationsAsRead = () => {
    setNotifications(prev => prev.map(notif => ({ ...notif, read: true })));
    setUnreadCount(0);
  };

  const clearNotifications = () => {
    setNotifications([]);
    setUnreadCount(0);
  };

  const updateLoan = (cid, updates) => {
    setLoans(prevLoans =>
      prevLoans.map(loan =>
        loan.cid === cid
          ? { ...loan, ...updates }
          : loan
      )
    );

    // Add notification for loan status changes
    if (updates.status === 'Approved' || updates.status === 'Declined') {
      const loan = loans.find(l => l.cid === cid);
      addNotification({
        cid,
        clientName: loan.name,
        type: 'status_change',
        status: updates.status,
        message: `Loan request for client ${loan.name} has been ${updates.status.toLowerCase()} by Operations Manager`
      });
    }
  };

  const updateLoanAmount = (cid, newAmount) => {
    const loan = loans.find(l => l.cid === cid);
    setLoans(prevLoans =>
      prevLoans.map(loan => {
        if (loan.cid === cid) {
          const updatedHistory = [...loan.loans.loanHistory];
          const latestRequestIndex = updatedHistory.findIndex(
            (loan, _, arr) =>
            new Date(loan.dateApplied).getTime() ===
            Math.max(...arr.map(l => new Date(l.dateApplied).getTime()))
          );
          
          if (latestRequestIndex !== -1) {
            updatedHistory[latestRequestIndex] = {
              ...updatedHistory[latestRequestIndex],
              amount: newAmount
            };
          }

          return {
            ...loan,
            loans: {
              ...loan.loans,
              loanHistory: updatedHistory
            }
          };
        }
        return loan;
      })
    );
  };

  // Trigger notification for loan amount changes
  if (loan) {
    addNotification({
      cid,
      clientName: loan.name,
      type: 'amount_change',
      amount: newAmount,
      message: `Loan amount for client ${loan.name} has been updated to ₱${newAmount.toLocaleString()} by Operations Manager`
    });
  }

  return (
    <LoanContext.Provider value={{
      loans,
      updateLoan,
      updateLoanAmount,
      notifications,
      unreadCount,
      markNotificationsAsRead,
      clearNotifications
    }}>
      {children}
    </LoanContext.Provider>
  );
}

export function useLoan() {
  const context = useContext(LoanContext);
  if (!context) {
    throw new Error('useLoan must be used within a LoanProvider');
  }
  return context;
}
