import React, { useState } from 'react';
import { View, Text, TextInput, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
import { useLoan } from '../../contexts/LoanContext';

export default function OpsPendingListScreen({ navigation }) {
  const { loans } = useLoan();
  const [search, setSearch] = useState('');

  const filtered = loans.filter(
    item =>
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.cid.includes(search)
  );

  const getLatestLoanRequest = (item) => {
    if (item.loans?.loanHistory && item.loans.loanHistory.length > 0) {
      const latest = [...item.loans.loanHistory].sort((a, b) => 
        new Date(b.dateApplied) - new Date(a.dateApplied)
      )[0];
      return latest.dateApplied;
    }
    return 'No date';
  };

  const renderItem = ({ item }) => (
    <View style={styles.row}>
      <View style={[styles.cellContainer, { flex: 1 }]}>
        <Text style={styles.cell}>{item.cid}</Text>
      </View>
      <View style={[styles.cellContainer, { flex: 3 }]}>
        <Text style={styles.cell}>{item.name}</Text>
      </View>
      <View style={[styles.cellContainer, { flex: 2 }]}>
        <Text style={[
          styles.cell,
          styles.centerText,
          (item.status && item.status.toLowerCase() === 'approved') && styles.approvedStatus,
          (item.status && item.status.toLowerCase() === 'declined') && styles.declinedStatus,
          (!item.status || item.status.toLowerCase() === 'pending') && styles.pendingStatus
        ]}>
          {item.status ?
            item.status.charAt(0).toUpperCase() + item.status.slice(1).toLowerCase()
            : 'Pending'}
        </Text>
      </View>
      <View style={[styles.cellContainer, { flex: 3 }]}>
        <Text style={[styles.cell, styles.centerText]}>{getLatestLoanRequest(item)}</Text>
      </View>
      <TouchableOpacity
        style={styles.viewBtn}
        onPress={() => {
          navigation.navigate('OpsPendingLoanDetail', {
            client: item,
            backScreen: 'OpsPendingList'
          });
        }}
      >
        <Text>View</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      {/* Header Row */}
      <View style={styles.headerRow}>
        <Text style={styles.title}>List of Pending Loan Approval</Text>
        <TouchableOpacity
          style={styles.dashboardBtn}
          onPress={() => navigation.replace('OpsDashboard')}
        >
          <Text style={styles.btnText}>Go to Dashboard</Text>
        </TouchableOpacity>
      </View>

      {/* Search */}
      <View style={styles.searchBar}>
        <TextInput
          style={styles.searchInput}
          placeholder="Search"
          value={search}
          onChangeText={setSearch}
        />
      </View>

      {/* Table Header */}
      <View style={styles.tableHeader}>
        <View style={[styles.headerContainer, { flex: 1 }]}>
          <Text style={styles.headerCell}>CID</Text>
        </View>
        <View style={[styles.headerContainer, { flex: 3 }]}>
          <Text style={styles.headerCell}>Full Name</Text>
        </View>
        <View style={[styles.headerContainer, { flex: 2 }]}>
          <Text style={[styles.headerCell, styles.centerText]}>Loan Status</Text>
        </View>
        <View style={[styles.headerContainer, { flex: 3 }]}>
          <Text style={[styles.headerCell, styles.centerText]}>Date Applied</Text>
        </View>
        <View style={[styles.headerContainer, { flex: 1 }]}>
          <Text style={styles.headerCell}></Text>
        </View>
      </View>

      <FlatList
        data={filtered}
        keyExtractor={(item) => item.cid}
        renderItem={renderItem}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  cellContainer: {
    paddingHorizontal: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
  headerContainer: {
    paddingHorizontal: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
  centerText: {
    textAlign: 'center',
    width: '100%',
  },
  approvedStatus: {
    color: '#2ecc71',
    fontWeight: 'bold',
  },
  pendingStatus: {
    color: '#000000',
    fontWeight: 'bold',
  },
  declinedStatus: {
    color: '#e74c3c',
    fontWeight: 'bold',
  },
  container: {
    padding: 20,
    backgroundColor: '#f6f6f6',
    flex: 1,
  },
  headerRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  title: {
    fontSize: 22,
    fontWeight: 'bold',
  },
  dashboardBtn: {
    backgroundColor: '#007bff',
    paddingVertical: 10,
    paddingHorizontal: 16,
    borderRadius: 6,
  },
  btnText: {
    color: '#fff',
    fontWeight: '600',
  },
  searchBar: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  searchInput: {
    backgroundColor: '#ddd',
    borderRadius: 6,
    padding: 10,
    flex: 1,
  },
  tableHeader: {
    flexDirection: 'row',
    backgroundColor: '#dcdcdc',
    paddingVertical: 10,
    paddingHorizontal: 6,
    marginBottom: 4,
  },
  headerCell: {
    fontWeight: 'bold',
    fontSize: 15,
    paddingHorizontal: 10,
  },
  row: {
    flexDirection: 'row',
    paddingVertical: 12,
    borderBottomWidth: 1,
    borderColor: '#ccc',
    alignItems: 'center',
  },
  cell: {
    fontSize: 14,
  },
  viewBtn: {
    backgroundColor: '#ccc',
    paddingVertical: 6,
    paddingHorizontal: 16,
    borderRadius: 6,
  },
});
