// ... (keep all the imports and component code the same until the styles)

const styles = StyleSheet.create({
  // ... (keep all other styles the same)
  backBtn: {
    backgroundColor: '#f0f0f0',
    paddingVertical: Platform.select({ web: 10, default: 8 }),
    paddingHorizontal: Platform.select({ web: 16, default: 12 }),
    borderRadius: 8,
  },
  refreshBtn: {
    backgroundColor: '#28a745',
    paddingVertical: Platform.select({ web: 10, default: 8 }),
    paddingHorizontal: Platform.select({ web: 16, default: 12 }),
    borderRadius: 8,
  },
  actionBtnText: {
    // Change color based on the button type
    color: ({ style }) => style?.backgroundColor === '#f0f0f0' ? '#333' : '#fff',
    fontSize: Platform.select({ web: 14, default: 12 }),
    fontWeight: '600',
  },
  // ... (keep all other styles the same)
});
