import React, { createContext, useState, useContext } from 'react';

const LoanContext = createContext();

const initialLoans = [
  {
    cid: '001',
    name: 'Justin Brown',
    status: 'Pending',
    date: 'June 10, 2025 11:30am',
    email: 'justin@example.com',
    number: '+639123456789',
    address: '123 Main St, Metro Manila',
    loan: {
      requested: '₱50,000',
      recommended: '₱100,000'
    }
  },
  {
    cid: '002',
    name: 'Lebron Black',
    status: 'Pending',
    date: 'June 11, 2025 10:30am',
    email: 'lebron@example.com',
    number: '+639234567890',
    address: '456 Oak St, Metro Manila',
    loan: {
      requested: '₱75,000',
      recommended: '₱150,000'
    }
  },
  {
    cid: '003',
    name: 'James White',
    status: 'Pending',
    date: 'June 12, 2025 9:15am',
    email: 'james@example.com',
    number: '+639345678901',
    address: '789 Pine St, Metro Manila',
    loan: {
      requested: '₱100,000',
      recommended: '₱200,000'
    }
  },
  {
    cid: '004',
    name: 'Monke de lufe',
    status: 'Pending',
    date: 'June 12, 2025 9:45am',
    email: 'monke@example.com',
    number: '+639456789012',
    address: '321 Cedar St, Metro Manila',
    loan: {
      requested: '₱60,000',
      recommended: '₱120,000'
    }
  },
];

export function LoanProvider({ children }) {
  const [loans, setLoans] = useState(initialLoans);

  const updateLoan = (cid, updates) => {
    setLoans(prevLoans =>
      prevLoans.map(loan =>
        loan.cid === cid
          ? { ...loan, ...updates }
          : loan
      )
    );
  };

  const updateLoanAmount = (cid, newAmount) => {
    setLoans(prevLoans =>
      prevLoans.map(loan =>
        loan.cid === cid
          ? { ...loan, loan: { ...loan.loan, requested: `₱${parseInt(newAmount).toLocaleString()}` } }
          : loan
      )
    );
  };

  return (
    <LoanContext.Provider value={{ loans, updateLoan, updateLoanAmount }}>
      {children}
    </LoanContext.Provider>
  );
}

export function useLoan() {
  const context = useContext(LoanContext);
  if (!context) {
    throw new Error('useLoan must be used within a LoanProvider');
  }
  return context;
}
