import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, ScrollView, StyleSheet } from 'react-native';

const defaultClient = {
  name: 'N/A',
  age: 0,
  cid: '',
  email: 'N/A',
  number: 'N/A',
  address: 'N/A',
  input: {
    paymentHistory: 0,
    creditUtilization: 0,
    creditHistoryLength: 0,
    creditMix: 0,
    newInquiries: 0
  },
  financial: {
    monthlyIncome: 0,
    monthlyExpenses: 0,
    assets: 0,
    dti: 0
  },
  employer: 'N/A',
  position: 'N/A',
  loans: {
    approved: {
      amount: 0,
      status: 'None'
    },
    pending: {
      amount: 0,
      status: 'None'
    },
    loanHistory: []
  },
  loanBalance: {
    amount: 0,
    dueDate: 'N/A'
  }
};

export default function OpsClientProfileScreen({ navigation, route }) {
  const [client, setClient] = useState(null);

  useEffect(() => {
    if (route.params?.client) {
      setClient({
        ...defaultClient,
        ...route.params.client
      });
    }
  }, [route.params?.client]);

  if (!client) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }

  const calculateCreditScore = () => {
    const weights = {
      paymentHistory: 0.35,
      creditUtilization: 0.30,
      creditHistoryLength: 0.15,
      creditMix: 0.10,
      newInquiries: 0.10
    };

    const input = client.input;
    const score = 
      (input.paymentHistory * weights.paymentHistory) +
      (input.creditUtilization * weights.creditUtilization) +
      (input.creditHistoryLength * weights.creditHistoryLength) +
      (input.creditMix * weights.creditMix) +
      (input.newInquiries * weights.newInquiries);
    
    return Math.round(score);
  };

  const getCreditRating = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 70) return 'Good';
    if (score >= 60) return 'Fair';
    if (score >= 50) return 'Poor';
    return 'Very Poor';
  };

  return (
    <ScrollView style={styles.container}>
      {/* Topbar */}
      <View style={styles.topbar}>
        <Text style={styles.logo}><Text style={{ color: '#0066ff' }}>Loan</Text>Siya</Text>
        <Text style={styles.role}>Operations Manager</Text>
        <Text style={styles.logout}>Logout</Text>
      </View>

      {/* Body */}
      <View style={styles.content}>
        {/* LEFT SIDE */}
        <View style={styles.left}>
          <Text style={styles.sectionTitle}>Client Profile</Text>
          <Text style={styles.subTitle}>Personal Details</Text>
          <Text><Text style={styles.bold}>Name:</Text> {client.name}</Text>
          <Text><Text style={styles.bold}>Age:</Text> {client.age}</Text>
          <Text><Text style={styles.bold}>CID:</Text> {client.cid}</Text>
          <Text><Text style={styles.bold}>Email:</Text> {client.email}</Text>
          <Text><Text style={styles.bold}>Number:</Text> {client.number}</Text>
          <Text><Text style={styles.bold}>Address:</Text> {client.address}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Employment Info</Text>
          <Text>Employer: {client.employer}</Text>
          <Text>Position: {client.position}</Text>

          <Text style={[styles.sectionTitle, { marginTop: 16 }]}>Financial Info</Text>
          <Text>Monthly Income: ₱{client.financial.monthlyIncome.toLocaleString()}</Text>
          <Text>Monthly Expenses: ₱{client.financial.monthlyExpenses.toLocaleString()}</Text>
          <Text>Assets: ₱{client.financial.assets.toLocaleString()}</Text>
          <Text>Debt to Income Ratio: {client.financial.dti}%</Text>
        </View>

        {/* RIGHT SIDE */}
        <View style={styles.right}>
          {/* Credit Assessment */}
          <View style={styles.creditBox}>
            <Text style={styles.sectionTitle}>Credit Assessment</Text>
            <Text style={styles.bold}>Credit Score</Text>
            <Text style={styles.score}>{calculateCreditScore()}</Text>
            <Text style={styles.rating}>{getCreditRating(calculateCreditScore())}</Text>
            
            <Text style={[styles.bold, { marginTop: 10 }]}>Risk Assessment</Text>
            <Text>Payment History Score: {client.input.paymentHistory}%</Text>
            <Text>Credit Utilization: {client.input.creditUtilization}%</Text>
            <Text>Credit History Length Score: {client.input.creditHistoryLength}%</Text>
            <Text>Credit Mix Score: {client.input.creditMix}%</Text>
            <Text>New Inquiries Score: {client.input.newInquiries}%</Text>
          </View>

          {/* Loan History */}
          <Text style={[styles.sectionTitle, { marginTop: 20 }]}>Loan History</Text>
          {client.loans.loanHistory.length > 0 ? (
            client.loans.loanHistory.map((loan, i) => (
              <View key={i} style={styles.loanHistoryRow}>
                <Text>Amount: ₱{loan.amount.toLocaleString()}</Text>
                <Text>Purpose: {loan.purpose}</Text>
                <Text>Date: {loan.dateApplied}</Text>
                <Text>Status: {loan.status}</Text>
                <Text>Paid: {loan.paid ? 'Yes' : 'No'}</Text>
              </View>
            ))
          ) : (
            <Text>No loan history</Text>
          )}

          {/* Current Balance */}
          <View style={styles.loanBox}>
            <Text style={styles.bold}>Current Balance</Text>
            <Text>Amount: ₱{client.loanBalance.amount.toLocaleString()}</Text>
            <Text>Due Date: {client.loanBalance.dueDate}</Text>
          </View>

          {/* Back Button */}
          <View style={styles.actionRow}>
            <TouchableOpacity 
              style={styles.backBtn} 
              onPress={() => navigation.goBack()}
            >
              <Text>← Back to List</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: { 
    backgroundColor: '#f6f6f6',
    flex: 1 
  },
  topbar: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    backgroundColor: '#ddd',
    padding: 20,
    alignItems: 'center',
  },
  logo: { 
    fontSize: 22, 
    fontWeight: 'bold' 
  },
  role: { 
    fontSize: 14 
  },
  logout: { 
    fontWeight: 'bold', 
    fontSize: 14 
  },
  content: { 
    flexDirection: 'row', 
    padding: 20 
  },
  left: {
    flex: 1.1,
    paddingRight: 10,
    borderRightWidth: 1,
    borderColor: '#ccc',
  },
  right: {
    flex: 1.3,
    paddingLeft: 15,
  },
  sectionTitle: {
    fontWeight: 'bold',
    fontSize: 16,
    marginBottom: 10,
  },
  subTitle: {
    fontWeight: 'bold',
    marginTop: 10,
    marginBottom: 6,
  },
  bold: {
    fontWeight: 'bold',
  },
  creditBox: {
    backgroundColor: '#e8e8e8',
    padding: 12,
    borderRadius: 8,
  },
  score: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#2ecc71',
  },
  rating: {
    fontSize: 16,
    color: '#666',
    marginBottom: 10,
  },
  loanHistoryRow: {
    backgroundColor: '#eaeaea',
    padding: 8,
    marginBottom: 5,
    borderRadius: 4,
  },
  loanBox: {
    backgroundColor: '#f0f0f0',
    padding: 12,
    marginTop: 20,
    borderRadius: 8,
  },
  actionRow: {
    flexDirection: 'row',
    justifyContent: 'flex-start',
    marginTop: 20,
  },
  backBtn: {
    backgroundColor: '#ddd',
    paddingVertical: 8,
    paddingHorizontal: 16,
    borderRadius: 6,
  }
});
